bool binds_to_all_interfaces = true;

for (const auto& bind_arg : args.GetArgs("-bind")) {
    if (!IsBindAll(bind_arg)) {
        binds_to_all_interfaces = false;
        break;
    }
}
connOptions.bind_on_any = binds_to_all_interfaces &&
                          args.GetArgs("-whitebind").empty();
if (ListeningOnAllInterfaces()) {
    Discover();
}
