+            }
+        }
+    }
+    SettingsSpan span;
+    if (auto* map = FindKey(settings.ro_config, "")) {
+        if (auto* values = FindKey(*map, name)) {
+            span = SettingsSpan(*values);
+        }
+    }
+    fn(Source(span).SetConfigFile(/* top_level= */ true));
