+void LegacyScriptPubKeyMan::SetType(OutputType type, bool internal) {}
+
+bool DescriptorScriptPubKeyMan::GetNewDestination(const OutputType type, CTxDestination& dest, std::string& error)
+{
+    if (!CanGetAddresses(internal)) {
+        error = "No private keys available";
+        return false;
+    }
+    {
+        LOCK(cs_desc_man);
+        if (descriptor.descriptor->IsSingleType() && type != address_type) {
+            throw std::runtime_error(std::string(__func__) + ": Types are inconsistent");
+        }
+
+        TopUp();
+
+        // Get the scriptPubKey from the descriptor
+        FlatSigningProvider out_keys;
+        std::vector<CScript> scripts_temp;
+        if (descriptor.cache.size() <= (unsigned int)(descriptor.next_index - descriptor.range_start) && !TopUp(1)) {
+            // We can't generate anymore keys
+            error = "Error: Keypool ran out, please call keypoolrefill first";
+            return false;
+        }
+        descriptor.descriptor->ExpandFromCache(descriptor.next_index, descriptor.cache[descriptor.next_index - descriptor.range_start], scripts_temp, out_keys);
+
+        if (descriptor.descriptor->IsSingleType()) {
+            Optional<OutputType> out_script_type = descriptor.descriptor->GetOutputType();
+            if (out_script_type && out_script_type == type) {
+                ExtractDestination(scripts_temp[0], dest);
+            } else {
+                throw std::runtime_error(std::string(__func__) + ": Types are inconsistent. Stored type does not match type of newly generated address");
+            }
+        } else {
+            // This is a combo descriptor which should not be an active descriptor
+            assert(false);
+        }
+        descriptor.next_index++;
+        WalletBatch(m_storage.GetDatabase()).WriteDescriptor(GetID(), descriptor);
+        return true;
+    }
+}
+
+isminetype DescriptorScriptPubKeyMan::IsMine(const CScript& script) const
+{
+    LOCK(cs_desc_man);
+    if (m_map_script_pub_keys.count(script) > 0) {
+        return ISMINE_SPENDABLE;
+    }
+    return ISMINE_NO;
+}
+
+bool DescriptorScriptPubKeyMan::CheckDecryptionKey(const CKeyingMaterial& master_key, bool accept_no_keys)
+{
+    {
+        LOCK(cs_desc_man);
+        if (!m_map_keys.empty())
+            return false;
+
+        bool keyPass = m_map_crypted_keys.empty(); // Always pass when there are no encrypted keys
+        bool keyFail = false;
+        CryptedKeyMap::const_iterator mi = m_map_crypted_keys.begin();
+        for (; mi != m_map_crypted_keys.end(); ++mi)
+        {
+            const CPubKey &pubkey = (*mi).second.first;
+            const std::vector<unsigned char> &crypted_secret = (*mi).second.second;
+            CKey key;
+            if (!DecryptKey(master_key, crypted_secret, pubkey, key))
+            {
+                keyFail = true;
+                break;
+            }
+            keyPass = true;
+            if (m_decryption_thoroughly_checked)
+                break;
+        }
+        if (keyPass && keyFail)
+        {
+            LogPrintf("The wallet is probably corrupted: Some keys decrypt but not all.\n");
+            throw std::runtime_error("Error unlocking wallet: some keys decrypt but not all. Your wallet file may be corrupt.");
+        }
+        if (keyFail || (!keyPass && !accept_no_keys))
+            return false;
+        m_decryption_thoroughly_checked = true;
+    }
+    return true;
+}
+
+bool DescriptorScriptPubKeyMan::Encrypt(const CKeyingMaterial& master_key, WalletBatch* batch)
+{
+    LOCK(cs_desc_man);
+    if (!m_map_crypted_keys.empty()) {
+        return false;
+    }
+
+    for (const KeyMap::value_type& key_in : m_map_keys)
+    {
+        const CKey &key = key_in.second;
+        CPubKey pubkey = key.GetPubKey();
+        CKeyingMaterial secret(key.begin(), key.end());
+        std::vector<unsigned char> crypted_secret;
+        if (!EncryptSecret(master_key, secret, pubkey.GetHash(), crypted_secret)) {
+            return false;
+        }
+        m_map_crypted_keys[pubkey.GetID()] = make_pair(pubkey, crypted_secret);
+        batch->WriteCryptedDescriptorKey(GetID(), pubkey, crypted_secret);
+    }
+    m_map_keys.clear();
+    return true;
+}
+
+bool DescriptorScriptPubKeyMan::GetReservedDestination(const OutputType type, bool internal, CTxDestination& address, int64_t& index, CKeyPool& keypool)
+{
+    LOCK(cs_desc_man);
+    std::string error;
+    bool result = GetNewDestination(type, address, error);
+    index = descriptor.next_index - 1;
+    return result;
+}
+
+void DescriptorScriptPubKeyMan::ReturnDestination(int64_t index, bool internal, const CTxDestination& addr)
+{
+    LOCK(cs_desc_man);
+    // Only return when the index was the most recent
+    if (descriptor.next_index - 1 == index) {
+        descriptor.next_index--;
+    }
+    WalletBatch(m_storage.GetDatabase()).WriteDescriptor(GetID(), descriptor);
+    NotifyCanGetAddressesChanged();
+}
+
+std::map<CKeyID, CKey> DescriptorScriptPubKeyMan::GetKeys() const
+{
+    AssertLockHeld(cs_desc_man);
+    if (m_storage.HasEncryptionKeys() && !m_storage.IsLocked()) {
+        KeyMap keys;
+        for (auto key_pair : m_map_crypted_keys) {
+            const CPubKey& pubkey = key_pair.second.first;
+            const std::vector<unsigned char>& crypted_secret = key_pair.second.second;
+            CKey key;
+            DecryptKey(m_storage.GetEncryptionKey(), crypted_secret, pubkey, key);
+            keys[pubkey.GetID()] = key;
+        }
+        return keys;
+    }
+    return m_map_keys;
+}
+
+bool DescriptorScriptPubKeyMan::TopUp(unsigned int size)
+{
+    LOCK(cs_desc_man);
+    unsigned int target_size;
+    if (size > 0)
+        target_size = size;
+    else
+        target_size = std::max(gArgs.GetArg("-keypool", DEFAULT_KEYPOOL_SIZE), (int64_t) 0);
+
+    // Make sure we have everything between range_start and range_end
+    int32_t range_size = descriptor.range_end - descriptor.range_start;
+    int32_t highest_cached_index = descriptor.range_start + descriptor.cache.size();
+    int32_t to_fill = std::max(range_size - (int)descriptor.cache.size(), 0);
+
+    // Fill up the rest of the pool
+    int missing_pool = std::max(std::max((int)target_size, 1) - (descriptor.range_end - descriptor.next_index), 0);
+    to_fill += missing_pool;
+
+    // If the descriptor is not ranged, we actually just want to fill the first cache item
+    if (!descriptor.descriptor->IsRange()) {
+        to_fill = descriptor.cache.size() == 0 ? 1 : 0;
+        missing_pool = 0;
+        descriptor.range_end = 1;
+        descriptor.range_start = 0;
+    }
+
+    FlatSigningProvider provider;
+    provider.keys = GetKeys();
+
+    WalletBatch batch(m_storage.GetDatabase());
+    uint256 id = GetID();
+    for (int32_t i = highest_cached_index; i < highest_cached_index + to_fill; ++i) {
+        FlatSigningProvider out_keys;
+        std::vector<CScript> scripts_temp;
+        std::vector<unsigned char> cache;
+        if (!descriptor.descriptor->Expand(i, provider, scripts_temp, out_keys, &cache)) return false;
+        // Add all of the scriptPubKeys to the scriptPubKey set
+        for (const CScript& script : scripts_temp) {
+            m_map_script_pub_keys[script] = i;
+        }
+        // Write the cache
+        if (!batch.WriteDescriptorCache(id, i, cache)) {
+            throw std::runtime_error(std::string(__func__) + ": writing cache item failed");
+        }
+        descriptor.cache.push_back(std::move(cache));
+    }
+    descriptor.range_end += missing_pool;
+    batch.WriteDescriptor(GetID(), descriptor);
+
+    // By this point, the cache size should be the size of the entire range
+    assert((uint32_t)descriptor.range_end - (uint32_t)descriptor.range_start == descriptor.cache.size());
+
+    NotifyCanGetAddressesChanged();
+    return true;
+}
+
+bool DescriptorScriptPubKeyMan::ClearDescriptorCache() {
+    LOCK(cs_desc_man);
+    WalletBatch batch(m_storage.GetDatabase());
+    uint256 id = GetID();
+    int32_t highest_cached_index = descriptor.range_start + descriptor.cache.size();
+    for (uint32_t i = highest_cached_index; i < (uint32_t)highest_cached_index + descriptor.cache.size(); ++i) {
+        if (!batch.EraseDescriptorCache(id, i)) {
+            throw std::runtime_error(std::string(__func__) + ": removing cache item failed");
+        }
+    }
+    descriptor.cache.clear();
+
+    return true;
+}
+
+void DescriptorScriptPubKeyMan::MarkUnusedAddresses(const CScript& script)
+{
+    LOCK(cs_desc_man);
+    if (IsMine(script)) {
+        int32_t index = m_map_script_pub_keys[script];
+        if (index >= descriptor.next_index) {
+            WalletLogPrintf("%s: Detected a used keypool item, mark all keypool items up to this item as used\n", __func__);
+            descriptor.next_index = index + 1;
+        }
+        if (!TopUp()) {
+            WalletLogPrintf("%s: Topping up keypool failed (locked wallet)\n", __func__);
+        }
+    }
+}
+
+void DescriptorScriptPubKeyMan::AddDescriptorKey(const CKey& key, const CPubKey &pubkey)
+{
+    LOCK(cs_desc_man);
+    WalletBatch batch(m_storage.GetDatabase());
+    if (!AddDescriptorKeyWithDB(batch, key, pubkey)) {
+        throw std::runtime_error(std::string(__func__) + ": writing descriptor private key failed");
+    }
+}
+
+bool DescriptorScriptPubKeyMan::AddDescriptorKeyWithDB(WalletBatch& batch, const CKey& key, const CPubKey &pubkey)
+{
+    AssertLockHeld(cs_desc_man);
+    assert(!m_storage.IsWalletFlagSet(WALLET_FLAG_DISABLE_PRIVATE_KEYS));
+
+    if (m_storage.HasEncryptionKeys()) {
+        if (m_storage.IsLocked()) {
+            return false;
+        }
+
+        std::vector<unsigned char> crypted_secret;
+        CKeyingMaterial secret(key.begin(), key.end());
+        if (!EncryptSecret(m_storage.GetEncryptionKey(), secret, pubkey.GetHash(), crypted_secret)) {
+            return false;
+        }
+
+        m_map_crypted_keys[pubkey.GetID()] = make_pair(pubkey, crypted_secret);
+        batch.WriteCryptedDescriptorKey(GetID(), pubkey, crypted_secret);
+        return true;
+    } else {
+        m_map_keys[pubkey.GetID()] = key;
+        return batch.WriteDescriptorKey(GetID(), pubkey, key.GetPrivKey());
+    }
+}
+
+bool DescriptorScriptPubKeyMan::SetupDescriptorGeneration(const CExtKey& master_key)
+{
+    LOCK(cs_desc_man);
+    assert(m_storage.IsWalletFlagSet(WALLET_FLAG_DESCRIPTORS));
+
+    // Ignore when there is already a descriptor
+    if (descriptor.descriptor) {
+        return false;
+    }
+
+    int64_t creation_time = GetTime();
+
+    CPubKey master_pubkey = master_key.key.GetPubKey();
+    assert(master_key.key.VerifyPubKey(master_pubkey));
+
+    // Get the fingerprint
+    CKeyID master_id = master_key.key.GetPubKey().GetID();
+    std::string fingerprint = HexStr(master_id.begin(), master_id.begin() + 4);
+
+    // Add the seed to the wallet
+    std::string desc_prefix;
+    std::string desc_suffix = "/*)";
+    CExtKey d1;
+    switch (address_type) {
+    case OutputType::LEGACY: {
+        master_key.Derive(d1, BIP32_HARDENED_KEY_LIMIT + 44);
+        desc_prefix = "pkh([" + fingerprint + "/44'";
+        break;
+    }
+    case OutputType::P2SH_SEGWIT: {
+        master_key.Derive(d1, BIP32_HARDENED_KEY_LIMIT + 49);
+        desc_prefix = "sh(wpkh([" + fingerprint + "/49'";
+        desc_suffix += ")";
+        break;
+    }
+    case OutputType::BECH32: {
+        master_key.Derive(d1, BIP32_HARDENED_KEY_LIMIT + 84);
+        desc_prefix = "wpkh([" + fingerprint + "/84'";
+        break;
+    }
+    default: assert(false);
+    }
+
+    // Mainnet derives at 0',testnet and regtest derive at 1'
+    CExtKey d2;
+    if (Params().IsTestChain()) {
+        d1.Derive(d2, BIP32_HARDENED_KEY_LIMIT + 1);
+        desc_prefix += "/1'";
+    } else {
+        d1.Derive(d2, BIP32_HARDENED_KEY_LIMIT + 0);
+        desc_prefix += "/0'";
+    }
+    // 3rd level is always 0'
+    CExtKey d3;
+    d2.Derive(d3, BIP32_HARDENED_KEY_LIMIT + 0);
+    std::string xpub = EncodeExtPubKey(d3.Neuter());
+
+    // Build descriptor string
+    std::string internal_path = internal ? "/1" : "/0";
+    std::string desc_str = desc_prefix + "/0']" + xpub + internal_path + desc_suffix;
+
+    // Make the descriptor
+    FlatSigningProvider keys;
+    std::string error;
+    std::unique_ptr<Descriptor> desc = Parse(desc_str, keys, error, false);
+    WalletDescriptor w_desc(std::move(desc), creation_time, 0, 0, 0);
+    descriptor = w_desc;
+
+    // Store the master private key, derived intermediate key, and descriptor
+    WalletBatch batch(m_storage.GetDatabase());
+    if (!AddDescriptorKeyWithDB(batch, master_key.key, master_key.key.GetPubKey())) {
+        throw std::runtime_error(std::string(__func__) + ": writing descriptor master rivate key failed");
+    }
+    if (!AddDescriptorKeyWithDB(batch, d3.key, d3.key.GetPubKey())) {
+        throw std::runtime_error(std::string(__func__) + ": writing descriptor intermediate private key failed");
+    }
+    if (!batch.WriteDescriptor(GetID(), descriptor)) {
+        throw std::runtime_error(std::string(__func__) + ": writing descriptor failed");
+    }
+
+    // TopUp
+    TopUp();
+
+    m_storage.UnsetBlankWalletFlag(batch);
+    return true;
+}
+
+bool DescriptorScriptPubKeyMan::IsHDEnabled() const
+{
+    LOCK(cs_desc_man);
+    return descriptor.descriptor->IsRange();
+}
+
+bool DescriptorScriptPubKeyMan::CanGetAddresses(bool internal)
+{
+    LOCK(cs_desc_man);
+    return HavePrivateKeys() || descriptor.next_index < descriptor.range_end;
+}
+
+bool DescriptorScriptPubKeyMan::HavePrivateKeys() const
+{
+    LOCK(cs_desc_man);
+    return m_map_keys.size() > 0 || m_map_crypted_keys.size() > 0;
+}
+
+int64_t DescriptorScriptPubKeyMan::GetOldestKeyPoolTime()
+{
+    return 0;
+}
+
+size_t DescriptorScriptPubKeyMan::KeypoolCountExternalKeys()
+{
+    if (internal) {
+        return 0;
+    }
+    return GetKeyPoolSize();
+}
+
+unsigned int DescriptorScriptPubKeyMan::GetKeyPoolSize() const
+{
+    LOCK(cs_desc_man);
+    return descriptor.range_end - descriptor.next_index;
+}
+
+int64_t DescriptorScriptPubKeyMan::GetTimeFirstKey() const
+{
+    LOCK(cs_desc_man);
+    return descriptor.creation_time;
+}
+
+std::unique_ptr<FlatSigningProvider> DescriptorScriptPubKeyMan::GetSigningProvider(const CScript& script, bool include_private) const
+{
+    LOCK(cs_desc_man);
+
+    // Find the index of the script
+    auto it = m_map_script_pub_keys.find(script);
+    if (it == m_map_script_pub_keys.end()) {
+        return nullptr;
+    }
+    int32_t index = it->second;
+
+    // Get the scripts, keys, and key origins for this script
+    std::unique_ptr<FlatSigningProvider> out_keys = MakeUnique<FlatSigningProvider>();
+    std::vector<CScript> scripts_temp;
+    if (!descriptor.descriptor->ExpandFromCache(index, descriptor.cache[index - descriptor.range_start], scripts_temp, *out_keys)) return nullptr;
