+bool DescriptorScriptPubKeyMan::SetupDescriptorGeneration(const CExtKey& master_key)
+{
+    LOCK(cs_desc_man);
+    assert(m_storage.IsWalletFlagSet(WALLET_FLAG_DESCRIPTORS));
+
+    // Ignore when there is already a descriptor
+    if (descriptor.descriptor) {
+        return false;
+    }
+
+    int64_t creation_time = GetTime();
+
+    CPubKey master_pubkey = master_key.key.GetPubKey();
+    assert(master_key.key.VerifyPubKey(master_pubkey));
+
+    // Get the fingerprint
+    CKeyID master_id = master_key.key.GetPubKey().GetID();
+    std::string fingerprint = HexStr(master_id.begin(), master_id.begin() + 4);
+
+    // Add the seed to the wallet
+    std::string desc_prefix;
+    std::string desc_suffix = "/*)";
+    CExtKey d1;
+    switch (address_type) {
+    case OutputType::LEGACY: {
+        master_key.Derive(d1, BIP32_HARDENED_KEY_LIMIT + 44);
+        desc_prefix = "pkh([" + fingerprint + "/44'";
+        break;
+    }
+    case OutputType::P2SH_SEGWIT: {
+        master_key.Derive(d1, BIP32_HARDENED_KEY_LIMIT + 49);
+        desc_prefix = "sh(wpkh([" + fingerprint + "/49'";
+        desc_suffix += ")";
+        break;
+    }
+    case OutputType::BECH32: {
+        master_key.Derive(d1, BIP32_HARDENED_KEY_LIMIT + 84);
+        desc_prefix = "wpkh([" + fingerprint + "/84'";
+        break;
+    }
+    default: assert(false);
+    }
+
+    // Mainnet derives at 0',testnet and regtest derive at 1'
+    CExtKey d2;
+    if (Params().IsTestChain()) {
+        d1.Derive(d2, BIP32_HARDENED_KEY_LIMIT + 1);
+        desc_prefix += "/1'";
+    } else {
+        d1.Derive(d2, BIP32_HARDENED_KEY_LIMIT + 0);
+        desc_prefix += "/0'";
+    }
+    // 3rd level is always 0'
+    CExtKey d3;
+    d2.Derive(d3, BIP32_HARDENED_KEY_LIMIT + 0);
+    std::string xpub = EncodeExtPubKey(d3.Neuter());
+
+    // Build descriptor string
+    std::string internal_path = internal ? "/1" : "/0";
+    std::string desc_str = desc_prefix + "/0']" + xpub + internal_path + desc_suffix;
+
+    // Make the descriptor
+    FlatSigningProvider keys;
+    std::string error;
+    std::unique_ptr<Descriptor> desc = Parse(desc_str, keys, error, false);
+    WalletDescriptor w_desc(std::move(desc), creation_time, 0, 0, 0);
+    descriptor = w_desc;
+
+    // Store the master private key, derived intermediate key, and descriptor
+    WalletBatch batch(m_storage.GetDatabase());
+    if (!AddDescriptorKeyWithDB(batch, master_key.key, master_key.key.GetPubKey())) {
+        throw std::runtime_error(std::string(__func__) + ": writing descriptor master rivate key failed");
+    }
+    if (!AddDescriptorKeyWithDB(batch, d3.key, d3.key.GetPubKey())) {
+        throw std::runtime_error(std::string(__func__) + ": writing descriptor intermediate private key failed");
+    }
+    if (!batch.WriteDescriptor(GetID(), descriptor)) {
+        throw std::runtime_error(std::string(__func__) + ": writing descriptor failed");
+    }
+
+    // TopUp
+    TopUp();
+
+    m_storage.UnsetBlankWalletFlag(batch);
+    return true;
+}
