+        for (const auto& opt : opts) {
+            argv.push_back(opt.c_str());
+        }
+        std::string error;
+        if (!args.ParseParameters(argv.size(), argv.data(), error)) {
+            throw std::runtime_error(error);
+        }
+        BOOST_CHECK_EQUAL(error, "");
+        Options options;
+        ReadOptions(args, options);
