events.body:
@practicalswift C++ is a shitshow when it comes to static initialization (order). You might have to manually set up everything in the "right" order:

```diff
diff --git a/src/test/fuzz/process_message.cpp b/src/test/fuzz/process_message.cpp
index dcf0809e52..92bbc2681e 100644
--- a/src/test/fuzz/process_message.cpp
+++ b/src/test/fuzz/process_message.cpp
@@ -80,15 +80,16 @@ const std::map<std::string, std::set<std::string>> EXPECTED_DESERIALIZATION_EXCE
     {"Unknown transaction optional data: iostream error", {"block", "blocktxn", "cmpctblock", "tx"}},
 };
 
-std::unique_ptr<RegTestingSetup> g_fuzzing_setup;
+const RegTestingSetup* g_setup;
 } // namespace
 
 void initialize()
 {
-    g_fuzzing_setup = MakeUnique<RegTestingSetup>();
+    static RegTestingSetup setup{};
+    g_setup = &setup;
 
     for (int i = 0; i < 2 * COINBASE_MATURITY; i++) {
-        MineBlock(g_fuzzing_setup->m_node, CScript() << OP_TRUE);
+        MineBlock(g_setup->m_node, CScript() << OP_TRUE);
     }
     SyncWithValidationInterfaceQueue();
     assert(ChainActive().Tip() != nullptr);
@@ -116,9 +117,9 @@ void test_one_input(const std::vector<uint8_t>& buffer)
     p2p_node.fSuccessfullyConnected = true;
     p2p_node.nVersion = PROTOCOL_VERSION;
     p2p_node.SetSendVersion(PROTOCOL_VERSION);
-    g_fuzzing_setup->m_node.peer_logic->InitializeNode(&p2p_node);
+    g_setup->m_node.peer_logic->InitializeNode(&p2p_node);
     try {
-        (void)ProcessMessage(&p2p_node, random_message_type, random_bytes_data_stream, GetTimeMillis(), Params(), g_fuzzing_setup->m_node.connman.get(), g_fuzzing_setup->m_node.banman.get(), std::atomic<bool>{false});
+        (void)ProcessMessage(&p2p_node, random_message_type, random_bytes_data_stream, GetTimeMillis(), Params(), g_setup->m_node.connman.get(), g_setup->m_node.banman.get(), std::atomic<bool>{false});
     } catch (const std::ios_base::failure& e) {
         const std::string exception_message{e.what()};
         const auto p = EXPECTED_DESERIALIZATION_EXCEPTIONS.find(exception_message);

