+    r = initnatpmp(&natpmp, /* detect gateway automatically */ 0, /* forced gateway - NOT APPLIED*/ 0);
+    if (r < 0) {
+        LogPrintf("NAT-PMP: initnatpmp() failed with %d error.\n", r);
+        closenatpmp(&natpmp);
+        return;
+    }
+
+    if (fDiscover) {
+        r = sendpublicaddressrequest(&natpmp);
+        if (r < 0) {
