+    r = sendnewportmappingrequest(&natpmp, NATPMP_PROTOCOL_TCP, port, port, /* remove a port mapping */ 0);
+    if (r < 0) {
+        LogPrintf("NAT-PMP: sendnewportmappingrequest(0) failed with %d error.\n", r);
+    }
