+    m_aad_pos += CHACHA20_POLY1305_AEAD_AAD_LEN;
+    if (m_aad_pos + CHACHA20_POLY1305_AEAD_AAD_LEN > CHACHA20_ROUND_OUTPUT) {
+        m_aad_pos = 0;
+        m_aad_seqnr++;
+    }
