+    tracker.ReceivedInv(peers[0], txid, ReqTime(now, delay)); // Announce from peer 0
+    tracker.ReceivedInv(peers[1], txid, ReqTime(now, delay)); // Announce from peer 1
+    if (delay) now += TEST_DELAY;
