+    tracker.ReceivedInv(peers[0], txid, ReqTime(now, delay)); // Announce from peer 0
+    now += std::chrono::microseconds{1};
+    tracker.ReceivedInv(peers[2], txid, ReqTime(now, delay)); // Slightly later, announce from peer 2
+    now += std::chrono::microseconds{1};
