 {
     #ifdef DEBUG_LOCKORDER
     bool prev = g_debug_lockorder_abort;
-    g_debug_lockorder_abort = false;
+    g_debug_lockorder_abort = true;
     #endif
 
     RecursiveMutex rmutex1, rmutex2;
     TestPotentialDeadLockDetected(rmutex1, rmutex2);
 
     Mutex mutex1, mutex2;
