+    for (const auto& tx : block.data->vtx) {
+        if (!tx->IsCoinBase()) {
+            for (const auto& input : tx->vin) {
+                items.emplace_back(input.prevout, pos);
+            }
+        }
+        pos.nTxOffset += ::GetSerializeSize(TX_WITH_WITNESS(*tx));
+    }
+
+    return EraseSpenderInfos(items);
+}
