+//! Subsitute for std::monostate that doesn't depend on std::variant.
+struct MonoState{};
+
+//! Error information only allocated if there are errors or warnings.
+template <typename F>
+struct ErrorInfo {
+    std::optional<std::conditional_t<std::is_same_v<F, void>, MonoState, F>> failure{};
