+    m_chain_start = chain_start;
+    m_minimum_required_work = minimum_required_work;
+    m_current_chain_work = chain_start->nChainWork;
+    m_current_height = chain_start->nHeight;
+    m_chain_start_locator = std::move(chain_start_locator);
