+            [](int64_t sum, const auto it) {return sum + it->second.GetTxSize();}));
+        assert(ancestor_package_fee == std::accumulate(ancestors.cbegin(), ancestors.cend(), 0,
+            [](CAmount sum, const auto it) {return sum + it->second.GetModifiedFee();}));
