+            // removing the to-be-replaced entries. Note that this is only calculating bump fees.
+            // RBF fee rules should be handled separately.
+            m_to_be_replaced.insert(ptx->GetHash());
+            // Remove descendants because they will be replaced as well. This case should be rare
+            // as the wallet won't normally attempt to replace transactions with descendants.
+            CTxMemPool::setEntries descendants;
+            mempool.CalculateDescendants(mempool.GetIter(ptx->GetHash()).value(), descendants);
+            for (const auto& desc_txiter : descendants) {
+                m_to_be_replaced.insert(desc_txiter->GetTx().GetHash());
+            }
+        }
+
+        if (mempool.exists(GenTxid::Txid(outpoint.hash))) {
+            // This UTXO is unconfirmed and in the mempool.
+            auto it = m_requested_outpoints_by_txid.find(outpoint.hash);
+            if (it != m_requested_outpoints_by_txid.end()) {
+                it->second.push_back(outpoint);
+            } else {
+                std::vector<COutPoint> outpoints_of_tx({outpoint});
+                m_requested_outpoints_by_txid.emplace(outpoint.hash, outpoints_of_tx);
+            }
+        } else {
+            // This UTXO is either confirmed or not yet submitted to mempool.
+            // If it's confirmed, no bump fee is required.
+            // If it's not yet submitted, we have no information, so return 0.
+            m_bump_fees.emplace(outpoint, 0);
+        }
