+// Compare by min(ancestor feerate, individual feerate), then iterator
+struct AncestorFeerateComparator
+{
+    template<typename I>
+    bool operator()(const I& a, const I& b) const {
+        const auto& ea = a->second;
+        const CFeeRate a_feerate =
+            ea.GetModFeesWithAncestors() * ea.GetTxSize() <= ea.GetModifiedFee() * ea.GetSizeWithAncestors() ?
+            CFeeRate(ea.GetModFeesWithAncestors(), ea.GetSizeWithAncestors()) :
+            CFeeRate(ea.GetModifiedFee(), ea.GetTxSize());
+        const auto& eb = b->second;
+        const CFeeRate b_feerate =
+            eb.GetModFeesWithAncestors() * eb.GetTxSize() <= eb.GetModifiedFee() * eb.GetSizeWithAncestors() ?
+            CFeeRate(eb.GetModFeesWithAncestors(), eb.GetSizeWithAncestors()) :
+            CFeeRate(eb.GetModifiedFee(), eb.GetTxSize());
+        if (a_feerate != b_feerate) {
+            return a_feerate > b_feerate;
+        }
+        // Compare by txid
+        return a->first < b->first;
+    }
