```
// Compare by min(ancestor feerate, individual feerate), then iterator.
//
// The ancestor feerate of a high-feerate tx should be reduced by its
// low-feerate ancestors, but the ancestor feerate of a low-feerate tx
// should not be increased by its high-feerate ancestors.
struct AncestorFeerateComparator
{
    template<typename I>
    bool operator()(const I& a, const I& b) const {
        auto min_feerate = [](const MiniMinerMempoolEntry& e) -> CFeeRate {
            const CAmount afee{e.GetModFeesWithAncestors()};
            const int64_t asize{e.GetSizeWithAncestors()};
            const CAmount fee{e.GetModifiedFee()};
            const int64_t size{e.GetTxSize()};
            // Comparing ancestor feerate with individual feerate:
            //     afee / asize <= fee / size
            // Avoid division and possible loss of precision by
            // multiplying both sides by the sizes:
            return afee * size < fee * asize ?
                       CFeeRate(afee, asize) :
                       CFeeRate(fee, size);
        };
        CFeeRate a_feerate{min_feerate(a->second)};
        CFeeRate b_feerate{min_feerate(b->second)};
        if (a_feerate != b_feerate) {
            return a_feerate > b_feerate;
        }
        // Compare by txid
        return a->first < b->first;
    }
};
