+    //               â”‚                 â”‚
+    //               â”‚   Grandparent   â”‚
+    //               â”‚    1700 vB      â”‚
+    //               â”‚    1700 sats    â”‚                    Target feerate: 10    s/vB
+    //               â”‚       1 s/vB    â”‚    GP Ancestor Set Feerate (ASFR):  1    s/vB
+    //               â”‚                 â”‚                           P1_ASFR:  9.84â€¯s/vB
+    //               â””â”€â”€â”€â”€â”€â”€â–²â”€â”€â”€â–²â”€â”€â”€â”€â”€â”€â”˜                           P2_ASFR:  2.47 s/vB
+    //                      â”‚   â”‚                                   C_ASFR: 10.27 s/vB
+    // â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”    â”‚   â”‚    â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
+    // â”‚               â”œâ”€â”€â”€â”€â”˜   â””â”€â”€â”€â”€â”¤              â”‚             â‡’ C_FR < TFR < C_ASFR
+    // â”‚   Parent 1    â”‚             â”‚   Parent 2   â”‚
+    // â”‚    200 vB     â”‚             â”‚    200 vB    â”‚
+    // â”‚  17000 sats   â”‚             â”‚   3000 sats  â”‚
+    // â”‚     85 s/vB   â”‚             â”‚     15 s/vB  â”‚
+    // â”‚               â”‚             â”‚              â”‚
+    // â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â–²â”€â”€â”€â”˜             â””â”€â”€â”€â–²â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜
+    //             â”‚                     â”‚
+    //             â”‚    â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”    â”‚
+    //             â””â”€â”€â”€â”€â”¤           â”œâ”€â”€â”€â”€â”˜
+    //                  â”‚   Child   â”‚
+    //                  â”‚  100 vB   â”‚
+    //                  â”‚  900 sats â”‚
+    //                  â”‚    9 s/vB â”‚
+    //                  â”‚           â”‚
+    //                  â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜
+    //
+    // We therefore calculate both the bump fee that is necessary to elevate
+    // the individual transaction to the target feerate:
+    //         target_feerate Ã— tx_size - tx_fees
+    // and the bump fee that is necessary to bump the entire ancestor set to
+    // the target feerate:
+    //         target_feerate Ã— ancestor_set_size - ancestor_set_fees
+    // By picking the maximum from the two, we ensure that a transaction meets
+    // both criteria.
+    for (const auto& [txid, outpoints] : m_requested_outpoints_by_txid) {
+        auto it = m_entries_by_txid.find(txid);
+        Assume(it != m_entries_by_txid.end());
+        if (it != m_entries_by_txid.end()) {
+            Assume(target_feerate.GetFee(it->second.GetSizeWithAncestors()) > std::min(it->second.GetModifiedFee(), it->second.GetModFeesWithAncestors()));
