+/** Test if query parameter exists in the provided uri. */
+bool QueryParameterExists(std::string_view uri, const std::string& key)
+{
+    auto uri_parsed{evhttp_uri_parse(uri.data())};
+    const bool exists{GetQueryParameterFromUri(uri_parsed, key).has_value()};
+    if(uri_parsed) evhttp_uri_free(uri_parsed);
+    return exists;
+}
+
+/** Test if query parameter exists in the provided uri and if its value matches the expected_value. */
+bool QueryParameterEquals(std::string_view uri, const std::string& key, std::string_view expected_value)
+{
+    auto uri_parsed{evhttp_uri_parse(uri.data())};
+    const auto param_value{GetQueryParameterFromUri(uri_parsed, key)};
+    const bool is_equal{param_value.has_value() && *param_value == expected_value};
+    if(uri_parsed) evhttp_uri_free(uri_parsed);
+    return is_equal;
 }
 
 BOOST_AUTO_TEST_CASE(test_query_parameters)
 {
     // No parameters
-    evhttp_uri* uri_parsed{GetURIParsed("localhost:8080/rest/headers/someresource.json")};
-    BOOST_CHECK(!GetQueryParameterFromUri(uri_parsed, "p1").has_value());
-    evhttp_uri_free(uri_parsed);
