+std::pair<unsigned int, unsigned int> ChainstateManager::GetPruneRange(
+    const Chainstate& chainstate, unsigned int prune_height)
+{
+    if (chainstate.m_chain.Height() <= 0) {
+        return {0, 0};
+    }
+    unsigned int prune_start{0};
+
