 static Span<const std::byte> StringBytes(std::string_view str)
 {
     return AsBytes<const char>({str.data(), str.size()});
@@ -203,7 +197,7 @@ BOOST_AUTO_TEST_CASE(db_cursor_prefix_byte_test)
