-        for (CNode* pnode : m_nodes) {
-            if (pnode->GetId() == *node_id_to_evict) {
-                LogPrint(BCLog::NET, "selected %s connection for eviction peer=%d; disconnecting\n", pnode->ConnectionTypeAsString(), pnode->GetId());
-                pnode->fDisconnect = true;
