-    WITH_LOCK(m_nodes_disconnected_mutex, disconnected_nodes_copy = m_nodes_disconnected);
-    for (CNode* pnode : disconnected_nodes_copy)
-    {
-        DeleteDisconnectedNode(pnode);
+        m_nodes.erase(std::remove_if(m_nodes.begin(),
+                                     m_nodes.end(),
+                                     [this](CNode* node) {
