+        Assert(tx1->GetHash() == tx2->GetHash());
+        TRANSACTIONS.emplace_back(tx1);
+        TRANSACTIONS.emplace_back(tx2);
+        outpoints_index += 1;
+    }
+    // 2 parents 1 child
