+    // TIME_SKIPS[N] for N=0..15 is just N microseconds.
+    for (; i < 16; ++i) {
+        TIME_SKIPS[i] = std::chrono::microseconds{i};
+    }
+    // TIME_SKIPS[N] for N=16..127 has randomly-looking but roughly exponentially increasing values up to
