-                return std::make_unique<RawDescriptor>(script);
-            }
+            if (sub) return std::make_unique<WSHDescriptor>(std::move(sub));
         }
     }
     if (txntype == TxoutType::WITNESS_V1_TAPROOT && ctx == ParseScriptContext::TOP) {
