+{
+    auto tweaked_scan_seckey{scan_key};
+    tweaked_scan_seckey.TweakMultiply(outpoints_hash.begin());
+    CPubKey result = tweaked_scan_seckey.UnhashedECDH(sender_public_key);
+    assert(result.IsValid());
+    return result;
