+uint256 ComputeTweak(const CPubKey& ecdh_pubkey, const uint32_t output_index)
+{
+    HashWriter h;
+    h.write(Span{ecdh_pubkey});
+    unsigned char num[4];
+    WriteBE32(num, output_index);
+    h << num;
+    return h.GetSHA256();
