+
+uint256 ComputeSilentPaymentLabelTweak(const CKey& scan_key, const int m)
+{
+    HashWriter h{HASHER_LABEL};
+    unsigned char ser_m[4];
+    WriteBE32(ser_m, m);
+    h.write(scan_key);
+    h << ser_m;
+    return h.GetSHA256();
+}
+
+// BIP0352/Inputs
+uint256 ComputeInputHash(const std::vector<COutPoint>& tx_outpoints, const CPubKey& sum_input_pubkeys)
+{
+    HashWriter h{HASHER_INPUTS};
+    auto min_outpoint = std::min_element(tx_outpoints.begin(), tx_outpoints.end());
+
+    // Take the hash of the minimum outpoint (lexicographically) concatenated with the sum of the input public keys
+    h << *min_outpoint;
+    // We need to use the h.write method here instead of the << method for pubkeys because << operator for pubkeys also
+    // writes the byte size when serializing
+    h.write(sum_input_pubkeys);
+    return h.GetSHA256();
+}
+
+uint256 ComputeTweak(const CPubKey& ecdh_pubkey, const uint32_t output_index)
+{
+    HashWriter h{HASHER_SHARED_SECRET};
+    h.write(Span{ecdh_pubkey});
+    unsigned char num[4];
+    WriteBE32(num, output_index);
+    h << num;
+    return h.GetSHA256();
+}
+
+V0SilentPaymentDestination GenerateSilentPaymentLabeledAddress(const V0SilentPaymentDestination& receiver, const uint256& label)
+{
+    CPubKey labeled_spend_pubkey{receiver.m_spend_pubkey};
+    labeled_spend_pubkey.TweakAdd(label.data());
+    return V0SilentPaymentDestination{receiver.m_scan_pubkey, labeled_spend_pubkey};
+}
+
+CPubKey ComputeECDHSharedSecret(const CKey& scan_key, const CPubKey& sender_public_key, const uint256& deterministic_nonce)
+{
+    CKey tweaked_scan_seckey{scan_key};
+    tweaked_scan_seckey.TweakMultiply(deterministic_nonce.begin());
+    CPubKey result = tweaked_scan_seckey.UnhashedECDH(sender_public_key);
+    assert(result.IsValid());
+    return result;
+}
+
+CPubKey CreateOutput(const CKey& ecdh_scalar, const CPubKey& scan_pubkey, const CPubKey& spend_pubkey, const uint32_t output_index)
+{
+    CPubKey ecdh_pubkey = ecdh_scalar.UnhashedECDH(scan_pubkey);
+    uint256 shared_secret = ComputeTweak(ecdh_pubkey, output_index);
+    CPubKey silent_payment_output{spend_pubkey};
+    silent_payment_output.TweakAdd(shared_secret.begin());
+    return silent_payment_output;
