+        BOOST_CHECK_EQUAL(node->second.GetFlags(), CCoinsCacheEntry::DIRTY);
+        BOOST_CHECK_EQUAL(head.second.Next(), &(*node));
+
+        if (i > 0) {
+            BOOST_CHECK_EQUAL(&(*std::next(node)), node->second.Next());
+        } else {
+            BOOST_CHECK_EQUAL(nullptr, node->second.Next());
+        }
+    }
