+BOOST_AUTO_TEST_CASE(vector_release_test)
+{
+    std::vector<uint8_t> v1 = {1, 2, 3};
+    std::vector<uint8_t>{}.swap(v1);
+    v1.shrink_to_fit();
+    BOOST_CHECK(v1.capacity() == 0);
+
+    std::vector<uint8_t> v2 = {1, 2, 3};
+    v2 = std::vector<uint8_t>{};
+    v2.shrink_to_fit();
+    BOOST_CHECK(v2.capacity() == 0);
+
+    std::vector<uint8_t> v3 = {1, 2, 3};
+    v3.clear();
+    v3.shrink_to_fit();
+    BOOST_CHECK(v3.capacity() == 0);
+
+    std::vector<uint8_t> v4 = {1, 2, 3};
+    v4 = {};
+    v4.shrink_to_fit();
+    BOOST_CHECK(v4.capacity() == 0);
+
+    std::vector<uint8_t> v5 = {1, 2, 3};
+    std::vector<uint8_t>{}.swap(v5);
+    BOOST_CHECK(v5.capacity() == 0);
+
+    std::vector<uint8_t> v6 = {1, 2, 3};
+    v6 = std::vector<uint8_t>{};
+    BOOST_CHECK(v6.capacity() == 0);

