+    std::unordered_map<Txid, Wtxid, SaltedTxidHasher> v3_txid_to_wtxid;
+    // Map from txid to wtxid for non-v3 txns in the package.
+    std::unordered_map<Txid, Wtxid, SaltedTxidHasher> non_v3_txid_to_wtxid;
