+PeerManagerImpl::InvalidTxTask PeerManagerImpl::ProcessInvalidTx(const CTransactionRef& tx, NodeId nodeid, const TxValidationState& state)
+{
+    AssertLockHeld(cs_main);
+    AssertLockNotHeld(m_peer_mutex);
+    LogPrint(BCLog::MEMPOOLREJ, "%s (wtxid=%s) from peer=%d was not accepted: %s\n",
+             tx->GetHash().ToString(),
+             tx->GetWitnessHash().ToString(),
+             nodeid,
+             state.ToString());
+    // Maybe punish peer that gave us an tx
+    MaybePunishNodeForTx(nodeid, state);
+
+    switch (state.GetResult()) {
+    case TxValidationResult::TX_RESULT_UNSET:
+    case TxValidationResult::TX_NO_MEMPOOL:
+    {
+        // This function should only be called when a transaction fails validation.
+        Assume(false);
+        return InvalidTxTask::NONE;
+    }
+    case TxValidationResult::TX_UNKNOWN:
+    {
+        // Transaction was not validated; we don't know that it is invalid. Do not add it to any
+        // rejection caches or forget about it yet.
+        return InvalidTxTask::NONE;
+    }
+    case TxValidationResult::TX_WITNESS_STRIPPED:
+    {
+        // Do not add txids of witness transactions or witness-stripped
+        // transactions to the filter, as they can have been malleated;
+        // adding such txids to the reject filter would potentially
+        // interfere with relay of valid transactions from peers that
+        // do not support wtxid-based relay. See
+        // https://github.com/bitcoin/bitcoin/issues/8279 for details.
+        // We can remove this restriction (and always add wtxids to
+        // the filter even for witness stripped transactions) once
+        // wtxid-based relay is broadly deployed.
+        // See also comments in https://github.com/bitcoin/bitcoin/pull/18044#discussion_r443419034
+        // for concerns around weakening security of unupgraded nodes
+        // if we start doing this too early.
+        return InvalidTxTask::NONE;
+    }
+    case TxValidationResult::TX_MISSING_INPUTS:
