In https://github.com/bitcoin/bitcoin/commit/7c085554dce336eb1597ab2fc482163876a49270 "sign: Create MuSig2 signatures for known MuSig2 aggregate keys"

I have noticed there is some duplication in MuSig2 signing flows in the key path and script path spending. A common `SignMuSig2` function can help in removing this duplication and in removing risks of introducing discrepancies in the two spending paths - I checked that the tests pass. Also, should make the review easier by reducing diff.

<details open>
<summary>MuSig2 Signing Deduplication Diff</summary>

```diff
diff --git a/src/script/sign.cpp b/src/script/sign.cpp
index b183be8939..ed21ab88f8 100644
--- a/src/script/sign.cpp
+++ b/src/script/sign.cpp
@@ -262,6 +263,100 @@ static bool CreateSig(const BaseSignatureCreator& creator, SignatureData& sigdat
     return false;
 }
 
+static bool SignMuSig2(SigVersion sigversion, const SigningProvider& provider, const BaseSignatureCreator& creator, SignatureData& sigdata, const XOnlyPubKey& pk, KeyOriginInfo pk_info, const uint256* merkle_root, const uint256* leaf_hash, std::pair<XOnlyPubKey, uint256>* lookup_key, std::vector<unsigned char>& sig)
+{
+    for (const auto& [agg_pub, part_pks] : sigdata.musig2_pubkeys) {
+        if (part_pks.empty()) continue;
+
+        // Fill participant derivation path info
+        for (const auto& part_pk : part_pks) {
+            KeyOriginInfo part_info;
+            if (provider.GetKeyOrigin(part_pk.GetID(), part_info)) {
+                XOnlyPubKey xonly_part(part_pk);
+                auto it = sigdata.taproot_misc_pubkeys.find(xonly_part);
+                if (sigversion == SigVersion::TAPSCRIPT) {
+                    if (it == sigdata.taproot_misc_pubkeys.end()) {
+                        sigdata.taproot_misc_pubkeys.emplace(xonly_part, std::make_pair(std::set<uint256>({*leaf_hash}), part_info));
+                    } else {
+                        it->second.first.insert(*leaf_hash);
+                    }
+                } else {
+                    if (it == sigdata.taproot_misc_pubkeys.end()) {
+                        sigdata.taproot_misc_pubkeys.emplace(xonly_part, std::make_pair(std::set<uint256>(), part_info));
+                    }
+                }
+            }
+        }
+
+        std::vector<std::pair<uint256, bool>> tweaks;
+        CPubKey plain_pub = agg_pub;
+        if (XOnlyPubKey(agg_pub) != pk) {
+            if (pk_info.path.size() > 0) {
+                // Compute and compare fingerprint
+                CKeyID keyid = agg_pub.GetID();
+                if (!std::equal(pk_info.fingerprint, pk_info.fingerprint + sizeof(pk_info.fingerprint), keyid.data())) {
+                    continue;
+                }
+                // Get the BIP32 derivation tweaks
+                CExtPubKey extpub = CreateMuSig2SyntheticXpub(agg_pub);
+                for (const int i : pk_info.path) {
+                    auto& [t, xonly] = tweaks.emplace_back();
+                    xonly = false;
+                    if (!extpub.Derive(extpub, i, &t)) {
+                        return false;
+                    }
+                }
+                Assert(XOnlyPubKey(extpub.pubkey) == pk);
+                plain_pub = extpub.pubkey;
+            } else {
+                continue;
+            }
+        }
+
+        // Add the merkle root tweak
+        if (sigversion == SigVersion::TAPROOT && merkle_root) {
+            tweaks.emplace_back(pk.ComputeTapTweakHash(merkle_root->IsNull() ? nullptr : merkle_root), true);
+            std::optional<std::pair<XOnlyPubKey, bool>> tweaked = pk.CreateTapTweak(merkle_root->IsNull() ? nullptr : merkle_root);
+            if (!Assume(tweaked)) return false;
+            plain_pub = tweaked->first.GetCPubKeys().at(tweaked->second ? 1 : 0);
+        }
+
+        // First try to aggregate
+        if (creator.CreateMuSig2AggregateSig(part_pks, sig, agg_pub, plain_pub, leaf_hash, tweaks, sigversion, sigdata)) {
+            if (sigversion == SigVersion::TAPSCRIPT) {
+                sigdata.taproot_script_sigs[*lookup_key] = sig;
+            } else {
+                sigdata.taproot_key_path_sig = sig;
+            }
+            return true;
+        }
+        // Cannot aggregate, try making partial sigs for every participant
+        std::pair<CPubKey, uint256> pub_key_leaf_hash = (sigversion == SigVersion::TAPSCRIPT) ? std::make_pair(plain_pub, *leaf_hash) : std::make_pair(plain_pub, uint256());
+        for (const CPubKey& part_pk : part_pks) {
+            uint256 partial_sig;
+            if (creator.CreateMuSig2PartialSig(provider, partial_sig, agg_pub, plain_pub, part_pk, leaf_hash, tweaks, sigversion, sigdata) && Assume(!partial_sig.IsNull())) {
+                sigdata.musig2_partial_sigs[pub_key_leaf_hash].emplace(part_pk, partial_sig);
+            }
+        }
+        // If there are any partial signatures, exit early
+        auto partial_sigs_it = sigdata.musig2_partial_sigs.find(pub_key_leaf_hash);
+        if (partial_sigs_it != sigdata.musig2_partial_sigs.end() && !partial_sigs_it->second.empty()) {
+            return false;
+        }
+        // No partial sigs, try to make pubnonces
+        std::map<CPubKey, std::vector<uint8_t>>& pubnonces = sigdata.musig2_pubnonces[pub_key_leaf_hash];
+        for (const CPubKey& part_pk : part_pks) {
+            if (pubnonces.contains(part_pk)) continue;
+            std::vector<uint8_t> pubnonce = creator.CreateMuSig2Nonce(provider, agg_pub, plain_pub, part_pk, leaf_hash, merkle_root, sigversion, sigdata);
+            if (pubnonce.empty()) continue;
+            pubnonces[part_pk] = std::move(pubnonce);
+        }
+
+        if (sigversion == SigVersion::TAPROOT) break;
+    }
+    return false;
+}
