     {
-        // Otherwise, emulate 96-bit multiplication using two 64-bit multiplies.
-        int64_t low = int64_t{static_cast<uint32_t>(a)} * b;
-        int64_t high = (a >> 32) * b;
-        return {high + (low >> 32), static_cast<uint32_t>(low)};
+        // Emulate 96-bit multiplication using two 64-bit multiplies.
+        int64_t low_mul = int64_t{static_cast<uint32_t>(high)} * low;
+        int64_t high_mul = (high >> 32) * low;
+        return {high_mul + (low_mul >> 32), static_cast<uint32_t>(low_mul)};
+    }
