             client->m_disconnect = true;
             return;
         }
 
         ssize_t body_bytes_sent = SendBytes(node_id, MakeUCharSpan(res.m_body), /*will_send_more=*/true, err);
         if (body_bytes_sent < 0) {
             LogDebug(
