+
     // Get the HTTPClient
     auto client{WITH_LOCK(m_clients_mutex, return GetClientById(node_id);)};
     if (client == nullptr) {
         return;
     }
 
@@ -1149,26 +1152,26 @@ void HTTPServer::EventReadyToSend(NodeId node_id, bool& cancel_recv)
             LogDebug(
                 BCLog::HTTP,
