         }
 
         LogDebug(
             BCLog::HTTP,
@@ -1176,20 +1179,17 @@ void HTTPServer::EventReadyToSend(NodeId node_id, bool& cancel_recv)
             hdr_bytes_sent + body_bytes_sent,
