+    socklen_t len{sizeof(storage)};
+    if (!to.GetSockAddr(reinterpret_cast<sockaddr*>(&storage), &len)) {
+        errmsg = Untranslated(strprintf("Bind address family for %s not supported", to.ToStringAddrPort()));
+        return false;
+    }
+
+    std::unique_ptr<Sock> sock{CreateSock(to.GetSAFamily(), SOCK_STREAM, IPPROTO_TCP)};
+    if (!sock) {
+        errmsg = Untranslated(strprintf("Cannot create %s listen socket: %s",
+                                        to.ToStringAddrPort(),
+                                        NetworkErrorString(WSAGetLastError())));
+        return false;
+    }
