 #include <util/translation.h>
+#include <memusage.h>

 #include <algorithm>
 #include <chrono>
@@ -1281,6 +1282,13 @@ static int CommandLineRPC(int argc, char *argv[])

 MAIN_FUNCTION
 {
+    std::string s;
+    std::cout << "length (b)" << "," << "dynsize (b)" << std::endl;
+    for (int x=0; x<=200; ++x) {
+        std::cout << x << "," << memusage::DynamicUsage(s) << std::endl;
+        s += 'a';
+    }
+    exit(0);
 #ifdef WIN32
     common::WinCmdLineArgs winArgs;
     std::tie(argc, argv) = winArgs.get();
```

</details>

