-};
-
 // Added for Bitcoin Core. Wrapper for checking format strings at compile time.
 // Unlike ConstevalFormatString this supports std::string for runtime string
 // formatting without compile time checks.
 template <unsigned num_params>
-struct FormatStringCheck : FormatString {
-    consteval FormatStringCheck(const char* str) : FormatString{util::ConstevalFormatString<num_params>{str}.fmt} {}
-    FormatStringCheck(const std::string& str) : FormatString{str.c_str()} {}
