 {
-    Assert(std::countl_zero(static_cast<uint64_t>(mib)) >= 21); // Ensure sign bit is unset + enough zeros to shift.
+    if (mib < 0 || std::countl_zero(static_cast<uint64_t>(mib)) < 21) {
