+template<typename S>
+class BufferedReader
+{
+    S& m_stream;
+    std::vector<std::byte> m_buffer;
+    size_t m_pos; //!< Next m_buffer position to read.
+public:
+    BufferedReader(S& stream, size_t size=65536) : m_stream{stream}, m_buffer{size}, m_pos{size} {}
+    void read(std::span<std::byte> dst)
+    {
+        while (!dst.empty()) {
+            if (m_pos == m_buffer.size()) {
+                std::span<std::byte> data{m_buffer};
+                m_stream.read_buffer(data);
+                m_buffer.resize(data.size()); // shrink on eof
+                m_pos = 0;
+            }
+            if (m_buffer.size() == 0) throw std::ios_base::failure("BufferedReader::read: end of file");
+            size_t n = std::min(dst.size(), m_buffer.size() - m_pos);
+            std::copy(m_buffer.begin() + m_pos, m_buffer.begin() + m_pos + n, dst.begin());
+            m_pos += n;
+            dst = dst.subspan(n);
+        }
+    }
+
+    template <typename T>
+    BufferedReader& operator>>(T&& obj)
+    {
+        ::Unserialize(*this, obj);
+        return *this;
+    }
+};
+
+template<typename S>
+class BufferedWriter
+{
+    S& m_stream;
+    std::vector<std::byte> m_buffer;
+    size_t m_pos; //!< Next m_buffer position to write.
+public:
+    BufferedWriter(S& stream, size_t size=65536) : m_stream{stream}, m_buffer{size}, m_pos{0} {}
+    ~BufferedWriter() { flush(); }
+
+    void write(std::span<const std::byte> src)
+    {
+        while (!src.empty()) {
+            size_t n = std::min(src.size(), m_buffer.size() - m_pos);
+            std::copy(src.begin(), src.begin() + n, m_buffer.begin() + m_pos);
+            m_pos += n;
+            if (m_pos == m_buffer.size()) flush();
+            src = src.subspan(n);
+        }
+    }
+
+    void flush()
+    {
+         m_stream.write_buffer(std::span{m_buffer}.first(m_pos));
+         m_pos = 0;
+    }
+
+    template <typename T>
+    BufferedWriter& operator<<(const T& obj)
+    {
+        ::Serialize(*this, obj);
+        return *this;
+    }
+};
+
