         common::SettingsValue value = args.GetPersistentSetting("upnp");
         if (!value.isNull()) {
-            LogWarning("Option '-upnp' has a value in settings.json, replacing it with `-natpmp`");
+            LogWarning("Option '-upnp' has a value in bitcoin.conf or settings.json, replacing it with '-natpmp' in settings.json");
             args.LockSettings([&](common::Settings& settings) {
                 settings.rw_settings.erase("upnp");
-                if (value.isTrue()) {
+                if ((value.isBool() && value.isTrue()) || (value.isStr() && value.get_str() == "1")) {
