+    }
+    file.seek(BLOCK_FILES_LAST_BLOCK_POS, SEEK_SET);
+    file >> last_block;
+    DataStream data;
+    data << last_block;
+    data << BLOCK_FILES_LAST_BLOCK_POS;
+    uint32_t re_check = crc32c::Crc32c(UCharCast(data.data()), data.size());
+    uint32_t checksum;
+    file >> checksum;
