+    log_file << BLOCK_FILE_INFO_WRAPPER_SIZE;
+    log_file << uint64_t{fileInfo.size()};
+    constexpr size_t block_file_entry_size{BLOCK_FILE_INFO_WRAPPER_SIZE + FILE_POSITION_SIZE};
