     {
         const Coin& coin = inputs.AccessCoin(tx.vin[i].prevout);
         assert(!coin.IsSpent());
-        if (coin.out.scriptPubKey.IsPayToScriptHash()) {
-            nSigOps += CountP2SHSigOps(tx.vin[i].scriptSig);
-        }
+        nSigOps += CountP2SHSigOps(tx.vin[i].scriptSig, coin.out.scriptPubKey);
     }
     return nSigOps;
 }
--- a/src/policy/policy.cpp
+++ b/src/policy/policy.cpp
@@ -180,10 +180,9 @@ static bool CheckSigopsBIP54(const CTransaction& tx, const CCoinsViewCache& inpu
         // This means sigops in the spent scriptPubKey count toward the limit.
         // `fAccurate` means correctly accounting sigops for CHECKMULTISIGs(VERIFY) with 16 pubkeys
         // or fewer. This method of accounting was introduced by BIP16, and BIP54 reuses it.
+        sigops += prev_txo.scriptPubKey.CountSigOps(/*fAccurate=*/true);
         sigops += txin.scriptSig.CountSigOps(/*fAccurate=*/true);
-        sigops += prev_txo.scriptPubKey.IsPayToScriptHash() ?
-                      CountP2SHSigOps(txin.scriptSig) :
-                      prev_txo.scriptPubKey.CountSigOps(/*fAccurate=*/true);
+        sigops += CountP2SHSigOps(txin.scriptSig, prev_txo.scriptPubKey);
 
         if (sigops > MAX_TX_LEGACY_SIGOPS) {
             return false;
--- a/src/script/script.cpp
+++ b/src/script/script.cpp
@@ -180,8 +180,12 @@ unsigned int CScript::CountSigOps(bool fAccurate) const
     return n;
 }
 
-unsigned int CountP2SHSigOps(const CScript& scriptSig)
+unsigned int CountP2SHSigOps(const CScript& scriptSig, const CScript& scriptPubKey)
 {
+    if (!scriptPubKey.IsPayToScriptHash()) {
+        return 0;
+    }
+
     // This is a pay-to-script-hash scriptPubKey;
     // get the last item that the scriptSig
     // pushes onto the stack:
