+            if (!m_pubkey_args.at(1)->ToNormalizedString(*arg, spend_key_str, cache)) return false;
             break;
         case StringType::PRIVATE:
-            if (!spend_pubkey->ToPrivateString(*arg, tmp)) return false;
+            if (!m_pubkey_args.at(1)->ToPrivateString(*arg, spend_key_str)) return false;
